INSERT INTO `yqxs_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2012-02-07 01:46:30', '2012-02-07 01:46:30', '欢迎使用 WordPress。这是系统自动生成的演示文章。编辑或者删除它，然后开始您的博客！', '世界，你好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2012-02-07 01:46:30', '2012-02-07 01:46:30', '', 0, 'http://www.yqxscaiji.tk/?p=1', 0, 'post', '', 1),
(2, 1, '2012-02-07 01:46:30', '2012-02-07 01:46:30', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如，个人博客通常有类似这样的介绍：\n\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做 Jack 的狗。</blockquote>\n\n... 公司博客可以这样写：\n\n<blockquote>XYZ Doohickey 公司成立于 1971 年，自从建立以来，我们一直向社会贡献着优秀 doohicky。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\n\n您可以访问<a href="http://www.yqxscaiji.tk/wp-admin/">仪表盘</a>，删除本页面，然后添加您自己的内容。祝您使用愉快！', '示例页面', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2012-02-07 01:46:30', '2012-02-07 01:46:30', '', 0, 'http://www.yqxscaiji.tk/?page_id=2', 0, 'page', '', 0),
(3, 1, '2012-02-07 01:46:43', '0000-00-00 00:00:00', '', '自动草稿', '', 'auto-draft', 'open', 'open', '', '', '', '', '2012-02-07 01:46:43', '0000-00-00 00:00:00', '', 0, 'http://www.yqxscaiji.tk/?p=3', 0, 'post', '', 0),
